import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import Input from './components/Input';
import Button from './components/Button';
import Footer from './components/Footer';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (event) => {
    event.preventDefault(); // Prevent the form from being submitted traditionally
    try {
      const response = await fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      
      // Ensure that the response is in JSON format
      const data = await response.json();
      
      // Check if the HTTP status code is in the 200 range
      if (response.ok) {
        alert('Login Successful!');
        console.log(data); // Assuming `data` is the JSON object returned by the server
      } else {
        // Use the message from the server's response, or a default message
        throw new Error(data.message || 'Failed to login');
      }
    } catch (error) {
      // This will catch any error in the try block as well as JSON parsing errors
      console.error('Login error:', error);
      alert('Login failed: ' + (error.message || 'Unknown error'));
    }
  };

  return (
    <div>
      <Header title="Sign In" />
      <div className='container' id='container'>
        <div className='form-container sign-in'>
          <form onSubmit={handleLogin}>
            <span>Use your Email and Password to sign in</span>
            <Input type="email" placeholder="Correo Electronico" value={email} onChange={e => setEmail(e.target.value)} />
            <Input type="password" placeholder="Ingresa tu contraseÃ±a" value={password} onChange={e => setPassword(e.target.value)} />
            <Button type="submit">Sign in</Button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
