import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import Button from './components/Button';
import Footer from './components/Footer';
import Register from './Register';

// Custom Input Component
const Input = ({ type, placeholder, value, onChange }) => (
  <input type={type} placeholder={placeholder} value={value} onChange={onChange} />
);

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showRegisterView, setShowRegisterView] = useState(false);  // Nuevo estado para controlar la vista

  const handleLogin = async (event) => {
    // ... tu función de inicio de sesión existente ...
  };

  // Función para cambiar entre la vista de inicio de sesión y registro
  const toggleView = () => {
    setShowRegisterView(!showRegisterView);
  };

  return (
    <div>
      <Header title={showRegisterView ? "Register" : "Sign In"} />
      <div className='container' id='container'>
        {showRegisterView ? (
          // Mostrar la vista de registro
          <Register />
        ) : (
          // Mostrar la vista de inicio de sesión
          <div className='form-container sign-in'>
            <form onSubmit={handleLogin}>
              {/* ... tus campos de entrada y botón de inicio de sesión ... */}
            </form>
            {/* Agregar un botón o enlace para cambiar a la vista de registro */}
            <Button onClick={toggleView}>Register</Button>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
}

export default App;
