import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import Input from './components/Input';
import Button from './components/Button';
import Footer from './components/Footer';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('/login', {  // Usar solo '/login' gracias al proxy
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
        if (!response.ok) {
            const errorResponse = await response.text();  // or response.json() if response is in JSON format
            console.error('Server responded with:', errorResponse);
            throw new Error('Network response was not ok: ' + response.statusText);
        }

        const data = await response.json();
        console.log('Login Successful:', data);
        alert('Login Successful!');
    } catch (error) {
        console.error('Login error:', error);
        alert('Login failed: ' + error.message);
    }
};



  return (
    <div>
      <Header title="Sign In" />
      <div className='container' id='container'>
        <div className='form-container sign-in'>
          <form onSubmit={handleLogin}>
            <span>Use your Email and Password to sign in</span>
            <Input type="email" placeholder="Correo Electronico" value={email} onChange={e => setEmail(e.target.value)} />
            <Input type="password" placeholder="Ingresa tu contraseÃ±a" value={password} onChange={e => setPassword(e.target.value)} />
            <Button type="submit">Sign in</Button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
