import React, { useState } from 'react';
import './App.css';
import Header from './components/Header';
import Input from './components/Input';
import Button from './components/Button';
import Footer from './components/Footer';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3001/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      
      if (!response.ok) {
        // Attempt to read response text and parse it if it is JSON
        const errorText = await response.text();
        try {
          const errData = JSON.parse(errorText);
          throw new Error(errData.message || 'Unknown error occurred');
        } catch {
          throw new Error(errorText || `Network response was not ok: ${response.status} ${response.statusText}`);
        }
      }
      
      const data = await response.json();
      console.log('Login Successful:', data);
      alert('Login Successful!');
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + error.message);
    }
  };

  return (
    <div>
      <Header title="Sign In" />
      <div className='container' id='container'>
        <div className='form-container sign-in'>
          <form onSubmit={handleLogin}>
            <span>Use your Email and Password to sign in</span>
            <Input type="email" placeholder="Correo Electronico" value={email} onChange={e => setEmail(e.target.value)} />
            <Input type="password" placeholder="Ingresa tu contraseÃ±a" value={password} onChange={e => setPassword(e.target.value)} />
            <Button type="submit">Sign in</Button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
