// Register.js
import React, { useState } from 'react';
import { handleRegisterClick, handleLoginClick } from '../ContainerActions';  
import Button from '../components/Button';


const Register = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student'); // O el valor predeterminado que desees

  const handleRegister = async (event) => {
    event.preventDefault();
    // Configurar el objeto usuario con los valores de los estados
    const user = {
      email,
      password,
      role
    };
    
    try {
      const response = await fetch('/register', {  // Cambia a tu endpoint de registro
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(user)
      });
      
      if (response.ok) {
        const data = await response.json();
        console.log('Registro exitoso:', data);
        // Aquí puedes redirigir al usuario o manejar el estado de inicio de sesión
      } else {
        const errorData = await response.json();
        console.error('Error en el registro:', errorData);
        alert(`Error en el registro: ${errorData.message}`);
      }
    } catch (error) {
      console.error('Error en la petición:', error);
      alert('Error en la petición: Ocurrió un error inesperado.');
    }
  };

   const toggleView = () => {
    handleRegisterClick();  
  };

  return (
    <div className="register-container">
      <form onSubmit={handleRegister}>
        <span>Usa tu correo electrónico UVG</span>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <select value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="student">Student</option>
          <option value="tutor">Tutor</option>
        </select>
        <button type="submit">Register</button>
      </form>
    </div>
  );
};

export default Register;
